# MEMPOOL FILTER
# Scans mempool for transactions to Uniswap v2 router
from web3 import Web3
import asyncio

endpoint = "~/.ethereum/geth/geth.ipc"
w3 = Web3(Web3.IPCProvider(endpoint))

UNISWAP_ADDY = "0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D"

