uniswap = w3.eth.contract(address=UNISWAP_ADDY, abi=UNISWAP_ABI)

def handle_event(event):
    try:
        # Parse txn hash
        txn = Web3.to_json(event).strip('"')
        # Grab txn using hash
        txn = w3.eth.get_transaction(txn)

        # Only filtering for txns to Uniswap v2 router, but could add more checks for routers here if desired
        if txn["to"] == UNISWAP_ADDY:
            print("[x] ----------------------------------------------------------------------------------------- -  -  -")
            for key, value in txn.items():
                if key == "input":
                    print(f"[+] {key}: {uniswap.decode_function_input(value)}")
                elif key in ["v", "r", "hash"]:
                    print(f"[+] {key}: {value.hex()}")
                else:
                    print(f"[+] {key}: {value}")
    except Exception:
        del(txn)

async def log_loop(event_filter, poll_interval):
    while True:
        for event in event_filter.get_new_entries():
            handle_event(event)
        
        await asyncio.sleep(poll_interval)

# Filter for pending txns
def filter_pool(filter_type):
    txn_filter = w3.eth.filter(filter_type)
    loop = asyncio.new_event_loop()
    asyncio.set_event_loop(loop)

    try:
        asyncio.run(log_loop(txn_filter, 2))
    finally:
        loop.close()
        asyncio.set_event_loop(None)

def main():
    filter_pool("pending")

if __name__ == "__main__":
    main()